/*
 * signup system based on the one I wrote in PHP for notes
 *
 * version 3 - using mongoDB
 */

/* events */
const EventEmitter = require("events");

const connect = require("./connect.js");
const password = require("../../password.js");
const validators = require("../../validators.js");

function attempt_signup(signup_data, ee, args){
    /*
     * function that will attempt to sign up a user to a local db
     * (specified in connect.js) using login credentials from signup_data
     * when the function is done, an event "signup" will be emitted for ee
     *
     * the callback will be called like this: (ret, args)
     * where ret is an object with success/failure and some messages
     * and args are arguments passed to this function
     *
     * signup_data: {
     *   "username": ...,
     *   "email": ...,
     *   "password": ...
     * }
     *
     * ee: event emitter that listens for "signup"
     * callback prototype: (ret, args)
     *
     * args: optional args that will be passed to ee.on("signup")
     *
     * ret: {
     *   "success": ..., (true/false)
     *   "messages": [..., ...] (optional array of messages)
     *   "session": { (optional, in case of success)
     *     "uid": ..., (number, user id)
     *     "username": ..., (string)
     *     "email": ..., (string)
     *     "password": ..., (string)
     *   }
     * }
     */

    /* check input */
    let ret = check_errors(signup_data);
    if (ret["success"] === false){
	ee.emit("signup", ret, args);
	return;
    }

    let connection;
    const signup_emitter = new EventEmitter();

    /*
     * connect to db
     * start the chain of async functions
     * */
    connect.connect_to_db(signup_emitter, "connection");

    /* when connection is done */
    signup_emitter.on("connection", function(ret){
	if (ret["success"] === false) {
	    ee.emit("signup", ret, args);
	    return;
	}
	connection = ret["connection"];
	/* check if username exists */
	check_username(signup_data, connection, signup_emitter, "username");
    });

    /* after username check */
    signup_emitter.on("username", function(ret){
	if (ret["success"] === false) {
	    connection.close();
	    ee.emit("signup", ret, args);
	    return;
	}
	/* add user to db */
	signup(signup_data, connection, signup_emitter, "signup");
    });

    /* when signup is done */
    signup_emitter.on("signup", function(ret){
	connection.close();
	ee.emit("signup", ret, args);
    });
}

function check_errors(signup_data){
    /*
     * check if username/password is empty and if email is valid
     * */
    let success = true;
    let messages = [];
    if (!signup_data["username"]){
	messages.push("Please enter a username");
	success = false;
    }
    if (!signup_data["password"]){
	messages.push("Please enter a password");
	success = false;
    }
    if (!signup_data["email"]){
	messages.push("Please enter an email");
	success = false;
    } else if (validators.validate_email(signup_data["email"]) === false){
	messages.push("Please enter a valid email address");
	success = false;
    }
    return {"success": success, "messages": messages};
}

function check_username(signup_data, connection, ee, event){
    /*
     * checks if username is already in the db
     */
    let filter = {username: signup_data["username"]};
    connection.collection("Users").find(filter).toArray(function(err, result){
	if (err){
	    let ret = {
		success: false,
		messages: ["Error retrieving users: " + err.message],
	    }
	    ee.emit(event, ret);
	    return;
	}
	if (result.length > 0){
	    let ret = {
		success: false,
		messages: ["Username already exists. Try a different one."],
	    }
	    ee.emit(event, ret);
	    return;
	}
	let ret = {
	    success: true,
	    messages: ["success"],
	};
	ee.emit(event, ret);
    });
}

function signup(signup_data, connection, ee, event, args){
    /*
     * inserts new user to db
     *
     * note
     * for the moment, user_data in hash_password() has the same
     * prototype as signup_data; this might change in the future
     * in which case this function needs to be updated
     */
    let hashed_password = password.hash_password(signup_data);
    connection.collection("Users").insertOne({
	username: signup_data["username"],
	email: signup_data["email"],
	password: hashed_password,
    }, function(err, result) {
	/*
	 * instead of doing these ifs I can use require("assert")
	 * but then the program will crash if anything goes wrong
	 * instead of returning a nice message
	 * */
	if (err){
	    let ret = {
		success: false,
		messages: ["MongoDB error: " + err.message],
	    }
	    ee.emit(event, ret);
	    return;
	}
	/* this seems like a weird check */
	if (result.insertedCount != 1){
	    let ret = {
		success: false,
		messages: ["MongoDB error"],
	    }
	    ee.emit(event, ret);
	    return;
	}
	/* log the user in and return success */
	let ret = {
	    "success": true,
	    "messages": ["success"],
	    "session": {
		"uid": result.insertedId,
		"username": signup_data["username"],
		"email": signup_data["email"],
		"password": hashed_password,
	    },
	};
	ee.emit(event, ret);
    });
}

/* exports */
module.exports.attempt_signup = attempt_signup;
